mapper.insert:
INSERT INTO d_user_msg
()
VALUES
(#{username}, MD5(#{password}), #{realname}, #{gender}, #{uuid}, #{referrer.uid}, #{tel}, now());

	SELECT LAST_INSERT_ID();

mapper.select:
SELECT uid, 
FROM d_user_msg
WHERE realname = #{realname}
AND is_valid IS TRUE;

SELECT *
FROM d_user_msg
WHERE referrer = #{uid}
AND is_valid IS TRUE;
